import React,{ useState, useEffect } from "react"
import axios from 'axios'

class Blog extends React.Component{
    constructor(props){
        super(props)
        this.state={
            blog : [],
            openComments : false,
            blog_id : ""
        }
    }
    componentDidMount= () => {
        axios.get('http://127.0.0.1:5000/blog').
        then (res =>{
            // console.log(res.data)
            this.setState({
                blog : res.data
            })
        }).catch(error => console.log(error))

      }

      showComments = () =>{
          alert()
        // axios.get('http://127.0.0.1:5000/comments').
        // then (res =>{
        //     // console.log(res.data)
        //     this.setState({
        //         blog : res.data
        //     })
        // }).catch(error => console.log(error))
      }

    render(){
        return (
            <div>
                {this.state.blog.map(ele => 
                    <>
                        <div className="card m-5">
                            <h5 className="card-title mt-3 ml-3">{ele.blog.title}</h5>
                            <div className="card-body">
                                <p>Published On : {ele.blog.publish_date}</p>
                                <div className="d-flex">

                                    <span className="badge badge-secondary ml-3 mb-3">{ele.category}</span>
                                    <span className="badge badge-secondary ml-3 mb-3">by {ele.username}</span>
                                </div>
                                <div className="card">
                                    <p className="card-text m-4">{ele.blog.blog_body}</p>
                                </div>
                            </div>
                            <div className="ml-3 mb-1">
                                <button className="btn btn-secondary" onClick={() =>  this.showComments(ele.blog_id)}>comment</button>
                            </div>

                        </div>

                    </>
                    )}
            </div>

        )
    }

}

export default Blog
